CREATE DEFINER=`root`@`localhost` FUNCTION `is_user_borrowing_book`(new_book_id INT, new_account_id INT) RETURNS tinyint(1)
    READS SQL DATA
    DETERMINISTIC
BEGIN
    DECLARE is_borrowing tinyint(1);

    -- Check if there is any record with the specified book_id, account_id, and return_date is NULL
    SELECT (COUNT(*) > 0)
    INTO is_borrowing
    FROM borrow
    WHERE borrow.book_id = new_book_id
      AND borrow.account_id = new_account_id
      AND borrow.return_date IS NULL;
      
    RETURN is_borrowing;
END ;;
