CREATE DEFINER=`root`@`localhost` PROCEDURE `login`(IN p_username VARCHAR(255), IN p_password VARCHAR(255))
BEGIN
    DECLARE v_user_id INT DEFAULT NULL;
    DECLARE v_user_role INT;
    DECLARE v_status INT;
    DECLARE v_newStatus VARCHAR(255);

    -- Perform the SQL query
    SELECT id, `role`, `status`, (SELECT name FROM status WHERE status.id = account.status) AS newStatus
    INTO v_user_id, v_user_role, v_status, v_newStatus
    FROM account
    WHERE username = p_username AND password = p_password;

    -- Check if the user exists and the status is not 0
    IF v_user_id IS NOT NULL THEN
        IF v_status != 0 THEN
            -- User account has a status other than 0
            SELECT 0 as user_id, JSON_OBJECT('error', CONCAT('Your account has been ', v_newStatus)) AS response;
        ELSE
            -- User logged in successfully
            SELECT v_user_id as user_id, v_user_role as `role`, JSON_OBJECT('success', 'Login successful') AS response;
            -- Start session or handle session here
        END IF;
    ELSE
        -- User not found or invalid credentials
        SELECT 0 as user_id, JSON_OBJECT('error', 'Your username or password is incorrect!') AS response;
    END IF;
END ;;
