CREATE DEFINER=`root`@`localhost` PROCEDURE `signup`(
    IN p_username VARCHAR(255),
    IN p_password VARCHAR(255),
    IN p_repeatPassword VARCHAR(255),
    OUT p_error_message VARCHAR(255)
)
BEGIN
    -- Check if username already exists
    SELECT COUNT(*) INTO @username_count FROM account WHERE username = p_username;

    IF @username_count > 0 THEN
        SET p_error_message = 'Username already exists.';
    ELSE
        -- Check if password and repeatPassword match
        IF p_password <> p_repeatPassword THEN
            SET p_error_message = 'Passwords do not match.';
        ELSE
            -- Insert new user into account table
            INSERT INTO account (username, `password`, `role`, `status`) 
            VALUES (p_username, p_password, 1, 0);

            -- Set success message
            SET p_error_message = NULL;
        END IF;
    END IF;
END ;;
